mapper.update:
	update board set
 	title = #{title}, contents = #{contents} , MODIFIEDTIME = TO_CHAR(SYSDATE, 'YYYY.MM.DD HH24:MI:SS') , division = #{division}
 	where bno = #{bno}

update board set
hit = hit+1
where bno = #{bno}

mapper.select:
select rno, rcontents, rwritingTime , memberId from reply
where bno = #{bno}
ORDER BY rno DESC
